----------------------------------------------------------  DDL for Function FIND_PHULUC_HIEULUC--------------------------------------------------------  CREATE OR REPLACE FUNCTION "THUEKENH"."FIND_PHULUC_HIEULUC" (tuyenkenh_id_ in varchar2,date_ in date) RETURN SYS_REFCURSOR ASl_cursor SYS_REFCURSOR;BEGIN	OPEN l_cursor FOR select t.* from phuluc t left join chitietphuluc_tuyenkenh t1 on t.id = t1.phuluc_idwhere t.deleted = 0 and t1.tuyenkenh_id = tuyenkenh_id_ and ( (t.ngayhethieuluc is null and t.ngayhieuluc  <= date_ ) or (t.ngayhethieuluc is not null and t.ngayhethieuluc >= date_ and t.ngayhieuluc <= date_ ) );	RETURN l_cursor;END FIND_PHULUC_HIEULUC;/----------------------------------------------------------  DDL for Function FN_GETMENUIDBYUSER--------------------------------------------------------  CREATE OR REPLACE FUNCTION "THUEKENH"."FN_GETMENUIDBYUSER" (idaccount_ in varchar2,idgroup_ in varchar2) RETURN SYS_REFCURSOR ASl_cursor SYS_REFCURSOR;----========================================================================-- @project: VMS quan ly kenh truyen dan-- @description: Lay danh sach menu ID cua user, tra ve dang List ID-- @author	nclong-- @date Nov 21, 2012-- @param-- @vesion 1.0-- @copyright nclong---- @date update ---- @user update---- @note: ----========================================================================BEGIN	open l_cursor for select m.ID from menu m,user_menu u where m.active = 1 and m.id=u.menuid and accountid=idaccount_ union select t3.ID from group_menu t1,vmsgroup t2,menu t3 where t3.active = 1 and t1.idmenu=t3.id and t1.idgroup=t2.id  and t2.active=1 and t2.id= idgroup_;	RETURN l_cursor;END FN_GETMENUIDBYUSER;/